package common.order.model;import com.mysql.cj.xdevapi.JsonString;import common.base.BaseModel;import jakarta.persistence.*;import org.hibernate.annotations.TypeDef;@TypeDef(name = "json", typeClass = JsonString.class)@Entity@Table(name = "order_items")public class OrderItemModel extends BaseModel {    @Column(name = "quantity")    private Long quantity;    @Column(name = "name")    private String name;    @Column(name = "description")    private String description;    @Column(name = "is_veg")    private Boolean isVeg;    @Column(name = "base_price")    private Double basePrice;    @Column(name = "tax")    private Double tax;    @Column(name = "selling_price")    private Double sellingPrice;    @ManyToOne(optional = false)    @JoinColumn(name = "order_id")    private OrderModel order;    public OrderItemModel() {    }    public Long getQuantity() {        return quantity;    }    public void setQuantity(Long quantity) {        this.quantity = quantity;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public Boolean getVeg() {        return isVeg;    }    public void setVeg(Boolean veg) {        isVeg = veg;    }    public Double getBasePrice() {        return basePrice;    }    public void setBasePrice(Double basePrice) {        this.basePrice = basePrice;    }    public Double getTax() {        return tax;    }    public void setTax(Double tax) {        this.tax = tax;    }    public Double getSellingPrice() {        return sellingPrice;    }    public void setSellingPrice(Double sellingPrice) {        this.sellingPrice = sellingPrice;    }    public OrderModel getOrderId() {        return order;    }    public void setOrder(OrderModel order) {        this.order = order;    }    public OrderItemModel(Long quantity,                          String name,                          String description,                          Boolean isVeg,                          Double basePrice,                          Double tax,                          Double sellingPrice,                          Long orderId) {        this.quantity = quantity;        this.name = name;        this.description = description;        this.isVeg = isVeg;        this.basePrice = basePrice;        this.tax = tax;        this.sellingPrice = sellingPrice;        this.order = order;    }    @Override    public String toString() {        return "OrderItemModel{" +                "quantity=" + quantity +                ", name='" + name + '\'' +                ", description='" + description + '\'' +                ", isVeg=" + isVeg +                ", basePrice=" + basePrice +                ", tax=" + tax +                ", sellingPrice=" + sellingPrice +                ", order=" + order +                ", id=" + id +                ", createdAt=" + createdAt +                ", createdBy='" + createdBy + '\'' +                ", updatedAt=" + updatedAt +                ", updatedBy='" + updatedBy + '\'' +                '}';    }}