package v2.order;import common.ApiResponse.ApiSuccess;import play.libs.Json;import play.mvc.Controller;import play.mvc.Http;import play.mvc.Result;import java.util.concurrent.CompletionStage;import static java.util.concurrent.CompletableFuture.supplyAsync;public class OrderController extends Controller {    public CompletionStage<Result> getOrderList(Http.Request request) {        return supplyAsync(() -> ok(Json.toJson(new ApiSuccess("success"))));    }}