package v2.pnr;import com.mysql.cj.log.Log;import common.ApiResponse.ApiSuccess;import play.Logger;import play.libs.Json;import play.mvc.Controller;import play.mvc.Http;import play.mvc.Result;import java.util.concurrent.CompletionStage;import static java.util.concurrent.CompletableFuture.supplyAsync;public class PNRController extends Controller {    final Logger.ALogger logger = Logger.of("application.PNRController");    public CompletionStage<Result> getPNRDetail(Http.Request request, Long pnr) {        logger.info("[" + request.id() + "] " + "json " + request);        return supplyAsync(() -> ok(Json.toJson(new ApiSuccess(pnr.toString()))));    }}